using FluentValidation; namespace ACCAI.Application.Voters; public sealed class VoterRegisterCommandValidator : AbstractValidator<VoterRegisterCommand> { public VoterRegisterCommandValidator(){ RuleFor(x=>x.Nid).NotEmpty().MinimumLength(8); RuleFor(x=>x.Origin).NotEmpty(); RuleFor(x=>x.DateOfBirth).LessThan(DateTime.UtcNow);} }